import React, { useState, useEffect } from 'react';
import { Box, Typography, IconButton, TextField, Button, Card } from '@mui/material';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';
import LikeButton from './LikeButton';
import DeleteButton from './DeleteButton';
import { shareContent } from './shareUtil';
import axios from 'axios';

const Comment = ({ postId }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [currentUserId, setCurrentUserId] = useState(null);
  const [showReplyBoxes, setShowReplyBoxes] = useState({}); // ใช้ object สำหรับการควบคุมการแสดง/ซ่อนช่องตอบกลับ

  // ตรวจสอบว่า userId ใน localStorage
  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      setCurrentUserId(userId);
      console.log("Loaded User ID in Comment:", userId);
    } else {
      console.error("User ID is not set in localStorage");
    }
    fetchComments();
  }, []);

  // ดึงคอมเมนต์จาก API
  const fetchComments = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/pawpaw_test/posts/${postId}/comments`);
      setComments(response.data.comments);
    } catch (error) {
      console.error('Error fetching comments:', error);
    }
  };

  // ฟังก์ชันสำหรับเพิ่มคอมเมนต์ใหม่
  const handleAddComment = async () => {
    if (newComment.trim() === '') return;
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `http://localhost:5000/pawpaw_test/posts/${postId}/comment`,
        { content: newComment },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setComments([response.data.comment, ...comments]);
      setNewComment('');
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  // ฟังก์ชันสำหรับเพิ่มรีพลาย (nested comment)
  const handleAddReply = async (parentCommentId) => {
    if (newComment.trim() === '') return;
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `http://localhost:5000/pawpaw_test/posts/${postId}/comment`,
        { content: newComment, parentCommentId },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchComments(); // รีเฟรชคอมเมนต์หลังจากโพสต์รีพลาย
      setNewComment('');
    } catch (error) {
      console.error('Error adding reply:', error);
    }
  };

  // ฟังก์ชันสำหรับแสดง/ซ่อนช่องกรอกรีพลาย
  const handleToggleReplyBox = (commentId) => {
    setShowReplyBoxes((prev) => ({
      ...prev,
      [commentId]: !prev[commentId], // สลับการแสดง/ซ่อนช่องกรอกรีพลายสำหรับ commentId นี้
    }));
  };

  // ฟังก์ชันสำหรับไลค์คอมเมนต์
  const handleLikeComment = async (commentId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please log in to like the comment.');
        return false;
      }
      const response = await axios.post(
        `http://localhost:5000/pawpaw_test/comments/${commentId}/like`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      if (response.data.success) {
        setComments((prevComments) =>
          prevComments.map((comment) =>
            comment._id === commentId
              ? { ...comment, likes: response.data.likes }
              : comment
          )
        );
        return true;
      }
    } catch (error) {
      console.error('Failed to like/unlike the comment', error);
    }
    return false;
  };

  // ฟังก์ชันสำหรับลบคอมเมนต์
  const handleDeleteComment = async (commentId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please log in to delete the comment.');
        return;
      }
      await axios.delete(`http://localhost:5000/pawpaw_test/comments/${commentId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setComments((prevComments) => prevComments.filter((comment) => comment._id !== commentId));
      alert('Comment deleted successfully.');
    } catch (error) {
      console.error('Failed to delete comment:', error);
    }
  };

  return (
    <Card sx={{ maxWidth: 800, margin: '20px auto', borderRadius: '16px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', padding: '16px' }}>
      {/* คำอธิบายหัวข้อของคอมเมนต์ */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
          Comments
        </Typography>
      </Box>

      {/* แสดงคอมเมนต์ */}
      {comments.map((comment) => (
        <Box key={comment._id} sx={{ marginTop: '16px', padding: '8px', border: '1px solid #e0e0e0', borderRadius: '8px', position: 'relative' }}>
          {/* ปุ่มลบสำหรับคอมเมนต์ของผู้ใช้ที่ล็อกอิน */}
          {currentUserId && comment.userId && currentUserId.toString() === comment.userId._id.toString() && (
            <Box sx={{ position: 'absolute', top: '8px', right: '8px' }}>
              <DeleteButton onDelete={() => handleDeleteComment(comment._id)} />
            </Box>
          )}

          <Typography variant="subtitle2" color="textSecondary">
            {comment.userId?.username || 'Unknown User'} •{' '}
            {new Date(comment.createdAt).toLocaleDateString()} •{' '}
            {new Date(comment.createdAt).toLocaleTimeString()}
          </Typography>
          <Typography variant="body1" sx={{ margin: '8px 0' }}>
            {comment.content}
          </Typography>

          {/* Actions (Like, Comment, Share) */}
          <Box sx={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <LikeButton
              likedByUser={currentUserId ? comment.likes.includes(currentUserId) : false}
              onLike={() => handleLikeComment(comment._id)}
              likeCount={comment.likes.length}
            />
            <IconButton onClick={() => handleToggleReplyBox(comment._id)}>
              <ReviewsIcon />
            </IconButton>

            {/* ช่องกรอกรีพลาย (Reply) */}
            {showReplyBoxes[comment._id] && (
              <Box sx={{ marginTop: '16px', display: 'flex', alignItems: 'center', gap: '8px' }}>
                <TextField
                  placeholder="Write a reply..."
                  variant="outlined"
                  size="small"
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                  sx={{ flexGrow: 1, marginRight: '8px' }}
                />
                <Button variant="contained" color="primary" onClick={() => handleAddReply(comment._id)}>
                  Reply
                </Button>
              </Box>
            )}

            <IconButton onClick={() => shareContent({ title: comment.content, url: window.location.href })}>
              <IosShareIcon />
            </IconButton>
          </Box>

          {/* การแสดงรีพลาย */}
          {comment.replies && comment.replies.length > 0 && (
            <Box sx={{ marginLeft: '16px', marginTop: '8px' }}>
              {/* ใช้ Reply component */}
              <Comment postId={postId} />
            </Box>
          )}
        </Box>
      ))}
    </Card>
  );
};

export default Comment;
