import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Post from '../components/Post'; // นำ component Post มาใช้เพื่อแสดงโพสต์ในหน้าโปรไฟล์

const Profile = () => {
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');
  
    if (!token) {
      alert('You are not logged in. Please log in first.');
      navigate('/login');
      return;
    }
  
    // ดึงข้อมูลผู้ใช้
    axios.get('http://localhost:5000/pawpaw_test/profile', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(response => {
      setUser(response.data);  // ตั้งค่าผู้ใช้
    })
    .catch(error => {
      console.error('Error fetching user data:', error);
      alert('Failed to load user profile. Please try again.');
      navigate('/login');
    });
  
    // ดึงโพสต์ของผู้ใช้
    axios.get(`http://localhost:5000/pawpaw_test/posts/user/${userId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(response => {
      setPosts(response.data.posts);  // ตั้งค่าโพสต์
    })
    .catch(error => {
      console.error('Error fetching user posts:', error);
    });
  
  }, [navigate]);  // ตรวจสอบให้แน่ใจว่า `user` และ `posts` ได้รับการโหลดก่อนใช้งาน
  

  if (!user) {
    return null; // ไม่แสดงอะไรถ้ายังไม่มีข้อมูลผู้ใช้หรือยังไม่ได้ล็อกอิน
  }

  return (
    <div style={styles.container}>
    <div style={styles.sidebar}>
      <div style={styles.profileHeader}>
        <h2>{user.username}</h2>
        {/* ตรวจสอบว่า user มีข้อมูลที่ต้องการหรือไม่ */}
        {user.email && <p><strong>Email:</strong> {user.email}</p>}
      </div>
      {/* แสดงข้อมูลที่มีค่าหรือไม่ */}
      <div style={styles.aboutSection}>
        <h3>About</h3>
        <p><strong>Email:</strong> {user.email || 'No email available'}</p>
      </div>
    </div>

    <div style={styles.posts}>
      <h3>Posts</h3>
      {posts && posts.length > 0 ? (
        posts.map(post => (
          post && post._id ? (
            <Post key={post._id} post={post} />
          ) : (
            <div key="no-post">No valid post available</div>
          )
        ))
      ) : (
        <p>No posts available. Try posting something about your pet!</p>
      )}
    </div>
  </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '20px',
  },
  sidebar: {
    width: '30%',
    backgroundColor: '#f4f4f4',
    padding: '20px',
    borderRadius: '8px',
  },
  profileHeader: {
    textAlign: 'center',
    marginBottom: '20px',
  },
  profileImage: {
    borderRadius: '50%',
    width: '120px',
    height: '120px',
    objectFit: 'cover',
  },
  aboutSection: {
    marginTop: '20px',
  },
  posts: {
    width: '65%',
    paddingLeft: '20px',
  },
};

export default Profile;
