//
import React, { useState } from 'react';
import { TextField, Button, Typography, IconButton } from '@mui/material';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';
import LikeButton from './LikeButton';
import axios from 'axios';

const Comment = ({ postId, comments, onAddComment }) => {
  const [replyInputs, setReplyInputs] = useState({});   // สำหรับเก็บข้อความตอบกลับแต่ละคอมเมนต์
  const [showReplyFields, setShowReplyFields] = useState({});

  // Handle submitting a comment for a post
  const handleCommentSubmit = async () => {
    if (inputComment.trim() !== '') {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          alert('Please log in to comment.');
          return;
        }

        // ส่งคอมเมนต์ใหม่ไปยัง backend
        const response = await axios.post(`http://localhost:5000/pawpaw_test/posts/${postId}/comment`, {
          content: inputComment
        }, {
          headers: { Authorization: `Bearer ${token}` }
        });

        if (response.data.success) {
          onAddComment(response.data.comment);
          setInputComment(''); // ล้างช่อง input ของคอมเมนต์
        }
      } catch (error) {
        console.error('Failed to create comment:', error);
      }
    }
  };

  // Handle submitting a reply to a comment
  const handleReplySubmit = async (commentId) => {
    const replyContent = replyInputs[commentId];
    if (replyContent && replyContent.trim() !== '') {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          alert('Please log in to reply.');
          return;
        }

        // ส่งการตอบกลับไปยัง backend
        const response = await axios.post(
          `http://localhost:5000/pawpaw_test/comments/${commentId}/reply`,
          { content: replyContent },
          { headers: { Authorization: `Bearer ${token}` } }
        );

        if (response.data.success) {
          // เพิ่มการตอบกลับใน state
          onAddComment(response.data.reply);
          setReplyInputs((prevState) => ({
            ...prevState,
            [commentId]: '', // ล้างช่อง input ของการตอบกลับ
          }));
        }
      } catch (error) {
        console.error('Failed to create reply:', error);
      }
    }
  };

  // Toggle reply input field visibility
  const toggleReplyField = (commentId) => {
    setShowReplyFields((prevState) => ({
      ...prevState,
      [commentId]: !prevState[commentId],
    }));
  };

  return (
  <div>
    {comments.map((comment) => (
      <div key={comment._id} style={{ marginTop: '1rem', padding: '10px', border: '1px solid #e0e0e0', borderRadius: '5px' }}>
        <Typography variant="body2" sx={{ marginBottom: '0.5rem' }}>
          {comment.username}: {comment.content}
        </Typography>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          {/* Like Button Component */}
          <LikeButton
            likedByUser={comment.likedByUser}
            onLike={() => {
              // Add like logic here
              console.log(`Liked comment with ID: ${comment._id}`);
            }}
            likeCount={comment.likes ? comment.likes.length : 0}
          />

          {/* Comment Icon to toggle reply field */}
          <IconButton
            aria-label="reply"
            onClick={() => toggleReplyField(comment._id)}
          >
            <ReviewsIcon />
          </IconButton>

          {/* Share Icon */}
          <IconButton aria-label="share">
            <IosShareIcon />
          </IconButton>

          {/* Reply input field */}
          {showReplyFields[comment._id] && (
            <div style={{ display: 'flex', alignItems: 'center', marginLeft: '1rem', flexGrow: 1 }}>
              <TextField
                label="Write a reply..."
                variant="outlined"
                value={replyInputs[comment._id] || ''}
                onChange={(e) => setReplyInputs((prevState) => ({
                  ...prevState,
                  [comment._id]: e.target.value,
                }))}
                size="small"
                sx={{ flexGrow: 1, marginRight: '0.5rem' }}
              />
              <Button
                variant="contained"
                color="primary"
                onClick={() => handleReplySubmit(comment._id)}
              >
                Reply
              </Button>
            </div>
          )}
        </div>
      </div>
    ))}
  </div>
);
};

export default Comment;
