import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { MDBContainer, MDBRow, MDBCol, MDBCard, MDBCardBody, MDBCardText, MDBCardImage, MDBTypography, MDBIcon } from 'mdb-react-ui-kit';
import Post from '../components/Post';


export default function Profile() {
  const [user, setUser] = useState({});
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();
  
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');  // เก็บ userId ไว้ใน localStorage
  
    if (!token || !userId) {
      alert('You are not logged in. Please log in first.');
      navigate('/login');
      return;
    }
  
    // ดึงข้อมูลโปรไฟล์จาก API
    axios.get('http://localhost:5000/pawpaw_test/profile', {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((response) => {
        setUser(response.data);
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
      });
  
    // ดึงโพสต์ของผู้ใช้จาก API
    axios.get(`http://localhost:5000/pawpaw_test/posts/user/${userId}`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((response) => {
        setPosts(response.data.posts); // ตั้งค่า posts
      })
      .catch((error) => {
        console.error('Error fetching posts:', error);
      });
  }, []);
  
  return (
    <MDBContainer className="py-5">
      <MDBRow>
        {/* ข้อมูลโปรไฟล์ */}
        <MDBCol lg="4">
          <MDBCard>
            <MDBRow className="g-0">
              <MDBCol md="12" className="gradient-custom text-center text-white">
                <MDBCardImage src={user.profileImage} alt="Avatar" className="my-5" style={{ width: '100px', height: '100px', borderRadius: '50%' }} fluid />
                <MDBTypography tag="h5">{user.username}</MDBTypography>
                <MDBCardText>{user.profile?.bio || 'No bio available'}</MDBCardText>
                <MDBIcon far icon="edit mb-5" />
              </MDBCol>
            </MDBRow>
            <MDBCardBody>
              <MDBTypography tag="h6">Information</MDBTypography>
              <MDBRow>
                <MDBCol size="6" className="mb-3">
                  <MDBTypography tag="h6">Email</MDBTypography>
                  <MDBCardText className="text-muted">{user.showEmail ? user.email : 'Email Hidden'}</MDBCardText>
                </MDBCol>
                <MDBCol size="6" className="mb-3">
                  <MDBTypography tag="h6">Phone</MDBTypography>
                  <MDBCardText className="text-muted">{user.profile?.phone || 'Not available'}</MDBCardText>
                </MDBCol>
              </MDBRow>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>

        {/* โพสต์ของผู้ใช้ */}
        <MDBCol lg="8">
          <h3>Posts</h3>
          {posts.length > 0 ? (
            posts.map((post) => <Post key={post._id} post={post} />)
          ) : (
            <p>No posts available. Please share your thoughts!</p>
          )}
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
}
