import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Avatar, Typography } from '@mui/material';
import Post from './components/Post'; // Import the Post component
import { useUser } from './components/UserContext'; // Import useUser from UserContext

const Forum = () => {
  const { user } = useUser(); // Get user data from context
  const [posts, setPosts] = useState([]); // Posts array
  const [postContent, setPostContent] = useState(''); // New post content

  const handlePostSubmit = (e) => {
    e.preventDefault();
    if (postContent) {
      const newPost = {
        id: posts.length + 1,
        content: postContent,
        username: user.username,
        profilePicture: user.profilePicture,
      };
      setPosts([newPost, ...posts]); // Add new post at the top
      setPostContent(''); // Clear the input
    }
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', padding: '20px' }}>
      {/* User Information and Post Input */}
      {user && (
        <Box sx={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
          <Avatar src={user.profilePicture} alt={user.username} sx={{ marginRight: '10px' }} />
          <Typography variant="h6">{user.username}</Typography>
        </Box>
      )}
      
      {/* Post Input Box */}
      <Box
        component="form"
        onSubmit={handlePostSubmit}
        sx={{
          backgroundColor: '#fff',
          borderRadius: '8px',
          padding: '15px',
          boxShadow: 2,
          marginBottom: '20px',
        }}
      >
        <TextField
          label="What's on your mind?"
          variant="outlined"
          fullWidth
          value={postContent}
          onChange={(e) => setPostContent(e.target.value)}
          multiline
          rows={3}
        />
        <Button type="submit" variant="contained" color="primary" sx={{ marginTop: '10px' }}>
          Post
        </Button>
      </Box>

      {/* Posts Display */}
      <Box>
        {posts.map((post) => (
          <Post key={post.id} post={post} />
        ))}
      </Box>
    </Box>
  );
};

export default Forum;
