import React, { useState, useEffect } from 'react';
import { Box, Typography, Fab } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import Post from '../components/Post';
import CreatePost from '../components/CreatePost';
import axios from 'axios'; // ใช้ axios สำหรับเรียก API

const Forum = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    // ดึงโพสต์จาก backend (ตัวอย่างการใช้ axios)
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/pawpaw_test/posts/all');
        setPosts(response.data.posts); // ตั้งค่า posts จาก backend
      } catch (error) {
        console.error('Failed to fetch posts:', error);
      }
    };

    fetchPosts(); // เรียกใช้ฟังก์ชันดึงโพสต์เมื่อคอมโพเนนต์ถูกเรียกใช้งาน
  }, []);

  const handleAddPost = (newPost) => {
    setPosts([newPost, ...posts]); // เพิ่มโพสต์ใหม่ที่ด้านบนของรายการโพสต์
  };

  return (
    <Box sx={{ padding: '20px' }}>
      <Typography variant="h4" sx={{ mb: 2 }}>
        Forum
      </Typography>
      <CreatePost onAddPost={handleAddPost} /> {/* ใช้ CreatePost สำหรับสร้างโพสต์ */}
      <Box sx={{ width: '100%', maxWidth: '800px' }}>
        {posts.map((post) => (
          <Post key={post.id} post={post} /> // แสดงโพสต์ที่มีอยู่
        ))}
      </Box>
      <Fab
        color="primary"
        aria-label="add"
        onClick={() => alert('Add new functionality coming soon')}
        sx={{ position: 'fixed', bottom: 20, right: 20 }}
      >
        <AddIcon />
      </Fab>
    </Box>
  );
};

export default Forum;
