import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import { Button, Box, Typography } from '@mui/material';

const ImageUpload = ({ onImagesChange }) => {
  const [image, setImage] = useState(null);
  const maxNumber = 1; // กำหนดให้เลือกได้แค่ 1 รูป

  // ฟังก์ชันที่จัดการการลากและวาง
  const onDrop = (acceptedFiles) => {
    const file = acceptedFiles[0];
    if (file) {
      setImage(file); // เก็บไฟล์ภาพ
      onImagesChange([file]); // ส่งไฟล์ไปยัง parent component
    }
  };

  // ฟังก์ชันที่ใช้จัดการการเลือกไฟล์
  const onImageUpload = () => {
    document.getElementById("file-input").click();
  };

  const { getRootProps, getInputProps } = useDropzone({
    onDrop,
    multiple: false, // ให้เลือกได้แค่ 1 รูป
    accept: 'image/*', // รองรับเฉพาะไฟล์ภาพ
  });

  // ฟังก์ชันสำหรับการเลือกไฟล์
  const onFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
      onImagesChange([file]); // ส่งไฟล์ไปยัง parent component
    }
  };

  return (
    <Box
      sx={{
        border: '2px dashed #ccc',
        padding: '20px',
        borderRadius: '8px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        backgroundColor: '#f9f9f9',
        cursor: 'pointer',
        marginBottom: '1rem',
      }}
      {...getRootProps()}
    >
      <input
        {...getInputProps()}
        id="file-input"
        style={{ display: 'none' }}
        onChange={onFileChange}
      />
      <Typography variant="body1" color="textSecondary" sx={{ marginBottom: '16px' }}>
        Drag & Drop or Click to upload an image
      </Typography>
      {image && (
        <Box sx={{ textAlign: 'center', marginTop: '16px' }}>
          <img src={URL.createObjectURL(image)} alt="uploaded" width="100" style={{ borderRadius: '8px' }} />
          <Button variant="outlined" color="error" onClick={() => setImage(null)} sx={{ marginTop: '8px' }}>
            Remove
          </Button>
        </Box>
      )}
    </Box>
  );
};

export default ImageUpload;
