import React from 'react';
import { Card, CardContent, CardActions, IconButton, Typography, Grid } from '@mui/material';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';

const Post = ({ post }) => {
  return (
    <Card sx={{ maxWidth: 600, margin: '20px auto' }}>
      <CardContent>
        <Typography variant="subtitle1" color="textSecondary">
          {post.username} â€¢ {new Date(post.createdAt).toLocaleDateString()}
        </Typography>
        <Typography variant="body1" sx={{ marginTop: '1rem' }}>
          {post.content}
        </Typography>
        
        {post.images && post.images.length > 0 && (
          <Grid container spacing={1} sx={{ marginTop: '1rem' }}>
            {post.images.slice(0, 4).map((image, index) => (
              <Grid item xs={6} key={index}>
                <img src={image.url} alt={`Post image ${index}`} style={{ width: '100%', borderRadius: '4px' }} />
              </Grid>
            ))}
          </Grid>
        )}
      </CardContent>
      <CardActions>
        <IconButton aria-label="like">
          <FavoriteBorderIcon /> <Typography variant="body2" sx={{ marginLeft: 0.5 }}>{post.likes}</Typography>
        </IconButton>
        <IconButton aria-label="comment">
          <ReviewsIcon />
        </IconButton>
        <IconButton aria-label="share">
          <IosShareIcon />
        </IconButton>
      </CardActions>
    </Card>
  );
};

export default Post;
