import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [isFollowing, setIsFollowing] = useState(false);

  useEffect(() => {
    // ดึงข้อมูลผู้ใช้จาก backend
    axios.get('/api/user/profile') // ปรับ endpoint ให้เหมาะสมกับ API ของคุณ
      .then(response => {
        setUser(response.data);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  const handleFollow = () => {
    setIsFollowing(!isFollowing);
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <div style={styles.profileHeader}>
          <img src={user.image || '/default-profile.png'} alt="Profile" style={styles.profileImage} /> {/* ใส่รูปโปรไฟล์ */}
          <h2>{user.username}</h2>
          <p>{user.profession || 'No profession provided'}</p>
          {/* ปุ่มติดตาม */}
          <button style={styles.followButton} onClick={handleFollow}>
            {isFollowing ? 'Unfollow' : 'Follow'}
          </button>
        </div>
        <div style={styles.aboutSection}>
          <h3>About</h3>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Phone:</strong> {user.phone || 'No phone number provided'}</p>
          <p><strong>Location:</strong> {user.location || 'No location provided'}</p>
        </div>
      </div>

      {/* Posts Section */}
      <div style={styles.posts}>
        <h3>Posts</h3>
        {user.posts && user.posts.length > 0 ? user.posts.map(post => (
          <div key={post.id} style={styles.post}>
            <h4>{post.title}</h4>
            <img src={post.image} alt="Post" style={styles.postImage} />
            <div style={styles.postStats}>
              <span>{post.likes} Likes</span>
              <span>{post.comments} Comments</span>
            </div>
          </div>
        )) : <p>No posts available</p>}
      </div>
    </div>
  );
};

export default Profile;
