import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Post from '../components/Post';

const Profile = () => {
  const [profileData, setProfileData] = useState(null);
  const [posts, setPosts] = useState([]); // เพิ่ม state สำหรับโพสต์
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    
    const userId = localStorage.getItem('userId');
    const username = localStorage.getItem('username'); // เก็บ username ด้วย

    // ตรวจสอบว่า userId และ username อยู่ใน localStorage หรือไม่
    if (!userId || !username) {
      alert('Please log in first');
      return;
    }

    // ฟังก์ชันสำหรับดึงข้อมูลโปรไฟล์
    const fetchProfileData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/pawpaw/user/profile', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setProfileData(response.data.data);
      } catch (error) {
        console.error('Error fetching profile data:', error);
      }
    };

    // ฟังก์ชันสำหรับดึงโพสต์ของผู้ใช้
    const fetchUserPosts = async () => {
        try {
          const response = await axios.get(`http://localhost:5000/pawpaw/posts/user/${userId}`, {
            headers: {
              Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
          });
          setPosts(response.data.posts); // บันทึกโพสต์ใน state
        } catch (error) {
          console.error('Error fetching user posts:', error);
        }
      };

    fetchProfileData();
    fetchUserPosts();
  }, []);  // [] ใช้เพื่อให้ fetch เรียกแค่ครั้งเดียวหลัง component mount

  return (
    <div style={{ display: 'flex', justifyContent: 'space-between', padding: '20px' }}>
      {/* Right side - Profile Data */}
      <div style={{ flex: 1, marginRight: '20px' }}>
        <h2>Profile</h2>
        {profileData && (
          <div>
            <p><strong>Username:</strong> {profileData.username}</p>
            <p><strong>Email:</strong> {profileData.email}</p>
            <p><strong>Phone:</strong> {profileData.phone || 'No phone number provided'}</p>
            <p><strong>Birthday:</strong> {profileData.birthday || 'Not provided'}</p>
            <p><strong>Bio:</strong> {profileData.bio || 'Not provided'}</p>
            <p><strong>Pets:</strong> {profileData.petTypes?.join(', ') || 'No pets added'}</p>
            <p><strong>Show Email:</strong> {profileData.showEmail ? 'Yes' : 'No'}</p>
            {profileData.profileImage && (
              <img src={`http://localhost:5000${profileData.profileImage}`} alt="Profile" />
            )}
          </div>
        )}
      </div>

      {/* Left side - User Posts */}
      <div style={{ flex: 2 }}>
        <h3>My Posts</h3>
        {posts.length > 0 ? (
          posts.map((post) => <Post key={post._id} post={post} />)
        ) : (
          <p>No posts available.</p>
        )}
      </div>
    </div>
  );
};

export default Profile;
