import React, { useState } from 'react';
import { TextField, Button, Typography, IconButton } from '@mui/material';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';
import LikeButton from './LikeButton';
import axios from 'axios';

const Comment = ({ postId, comments, onAddComment }) => {
  const [replyInputs, setReplyInputs] = useState({});
  const [showReplyFields, setShowReplyFields] = useState({});

  // Handle submitting a reply to a comment
  const handleReplySubmit = async (commentId) => {
    const replyContent = replyInputs[commentId];
    if (replyContent && replyContent.trim() !== '') {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          alert('Please log in to reply.');
          return;
        }

        // ส่งการตอบกลับไปยัง backend
        const response = await axios.post(
          `http://localhost:5000/pawpaw_test/posts/${postId}/comment`,
          {
            content: replyContent,
            parentCommentId: commentId,
          },
          { headers: { Authorization: `Bearer ${token}` } }
        );

        if (response.data.success) {
          // เพิ่มการตอบกลับใน state
          onAddComment(response.data.reply);
          setReplyInputs((prevState) => ({
            ...prevState,
            [commentId]: '', // ล้างช่อง input ของการตอบกลับ
          }));
        }
      } catch (error) {
        console.error('Failed to create reply:', error);
      }
    }
  };

  // Toggle reply input field visibility
  const toggleReplyField = (commentId) => {
    setShowReplyFields((prevState) => ({
      ...prevState,
      [commentId]: !prevState[commentId],
    }));
  };

  return (
    <div>
      {comments.map((comment) => (
        <div key={comment._id} style={{ marginTop: '1rem', padding: '10px', border: '1px solid #e0e0e0', borderRadius: '5px' }}>
          {/* Username, Date, and Time */}
          <Typography variant="subtitle2" color="textSecondary">
            {comment.userId?.username ? comment.userId.username : "Unknown"} • {new Date(comment.createdAt).toLocaleDateString()} • {new Date(comment.createdAt).toLocaleTimeString()}
          </Typography>

          {/* Content */}
          <Typography variant="body1" sx={{ marginBottom: '0.5rem' }}>
            {comment.content}
          </Typography>

          {/* Actions */}
          <div style={{ display: 'flex', alignItems: 'center' }}>
            {/* Like Button Component */}
            <LikeButton
              likedByUser={comment.likedByUser}
              onLike={() => {
                console.log(`Liked comment with ID: ${comment._id}`);
              }}
              likeCount={comment.likes ? comment.likes.length : 0}
            />

            {/* Reply Icon to toggle reply field */}
            <IconButton
              aria-label="reply"
              onClick={() => toggleReplyField(comment._id)}
              style={{ marginLeft: '0.5rem' }}
            >
              <ReviewsIcon />
            </IconButton>

            {/* Share Icon */}
            <IconButton aria-label="share" style={{ marginLeft: '0.5rem' }}>
              <IosShareIcon />
            </IconButton>
          </div>

          {/* Reply input field */}
          {showReplyFields[comment._id] && (
            <div style={{ display: 'flex', alignItems: 'center', marginLeft: '1rem', marginTop: '1rem' }}>
              <TextField
                label="Write a reply..."
                variant="outlined"
                value={replyInputs[comment._id] || ''}
                onChange={(e) => setReplyInputs((prevState) => ({
                  ...prevState,
                  [comment._id]: e.target.value,
                }))}
                size="small"
                sx={{ flexGrow: 1, marginRight: '0.5rem' }}
              />
              <Button
                variant="contained"
                color="primary"
                onClick={() => handleReplySubmit(comment._id)}
              >
                Reply
              </Button>
            </div>
          )}

          {/* Display replies */}
          {comment.replies && comment.replies.length > 0 && (
            <div style={{ marginLeft: '2rem', marginTop: '1rem' }}>
              {comment.replies.map((reply) => (
                <div key={reply._id} style={{ marginTop: '0.5rem', padding: '10px', border: '1px solid #f0f0f0', borderRadius: '5px' }}>
                  {/* Username, Date, and Time for Replies */}
                  <Typography variant="subtitle2" color="textSecondary">
                    {reply.userId?.username ? reply.userId.username : "Unknown"} • {new Date(reply.createdAt).toLocaleDateString()} • {new Date(reply.createdAt).toLocaleTimeString()}
                  </Typography>

                  {/* Reply Content */}
                  <Typography variant="body1" sx={{ marginBottom: '0.5rem' }}>
                    {reply.content}
                  </Typography>
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Comment;
