import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EditProfile = () => {
    const [profile, setProfile] = useState({
        phone: '',
        birthday: '',
        bio: '',
        petTypes: [],
        showEmail: false,
        profileImage: null
    });

    useEffect(() => {
        // ดึงข้อมูลโปรไฟล์ที่มีอยู่แล้วจาก API
        axios.get('/api/pawpaw_test/profile')
            .then(response => {
                setProfile({
                    phone: response.data.profile.phone,
                    birthday: response.data.profile.birthday,
                    bio: response.data.profile.bio,
                    petTypes: response.data.profile.petTypes,
                    showEmail: response.data.profile.showEmail,
                    profileImage: response.data.profile.profileImage
                });
            })
            .catch(error => {
                console.error('Error fetching profile data for edit:', error);
            });
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setProfile(prevState => ({ ...prevState, [name]: value }));
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setProfile(prevState => ({ ...prevState, profileImage: file }));
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append('phone', profile.phone);
        formData.append('birthday', profile.birthday);
        formData.append('bio', profile.bio);
        formData.append('petTypes', profile.petTypes);
        formData.append('showEmail', profile.showEmail);
        if (profile.profileImage) {
            formData.append('profileImage', profile.profileImage);
        }

        // ส่งข้อมูลไปที่ API สำหรับการอัปเดตโปรไฟล์
        axios.put('/api/pawpaw_test/profile', formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
        })
        .then(response => {
            alert('Profile updated successfully!');
            window.location.href = '/profile';  // หลังจากอัปเดตเสร็จ ให้กลับไปที่หน้า Profile
        })
        .catch(error => {
            console.error('Error updating profile:', error);
        });
    };

    return (
        <div>
            <h1>Edit Profile</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Phone</label>
                    <input
                        type="text"
                        name="phone"
                        value={profile.phone}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Birthday</label>
                    <input
                        type="date"
                        name="birthday"
                        value={profile.birthday}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Bio</label>
                    <textarea
                        name="bio"
                        value={profile.bio}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Pet Types</label>
                    <input
                        type="text"
                        name="petTypes"
                        value={profile.petTypes.join(', ')}
                        onChange={(e) => setProfile({ ...profile, petTypes: e.target.value.split(', ') })}
                    />
                </div>
                <div>
                    <label>Show Email</label>
                    <input
                        type="checkbox"
                        name="showEmail"
                        checked={profile.showEmail}
                        onChange={(e) => setProfile({ ...profile, showEmail: e.target.checked })}
                    />
                </div>
                <div>
                    <label>Profile Image</label>
                    <input
                        type="file"
                        name="profileImage"
                        onChange={handleFileChange}
                    />
                    {profile.profileImage && <img src={URL.createObjectURL(profile.profileImage)} alt="Profile" width={100} />}
                </div>
                <button type="submit">Save Changes</button>
            </form>
        </div>
    );
};

export default EditProfile;
