import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Post from '../components/Post'; // ใช้ Post component ในการแสดงโพสต์
import EditProfile from '../components/EditProfile'; // ใช้ EditProfile component ในการแก้ไขข้อมูลโปรไฟล์

const Profile = () => {
  const [user, setUser] = useState({
    _id: localStorage.getItem('userId'),
    username: localStorage.getItem('username'),
  });
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();
  const [isEditing, setIsEditing] = useState(false); // ใช้เพื่อจัดการโหมดแก้ไขโปรไฟล์

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');

    // ถ้าผู้ใช้ไม่ได้ล็อกอิน จะไม่สามารถเข้าถึงหน้าโปรไฟล์ได้
    if (!token || !userId) {
      alert('You are not logged in. Please log in first.');
      navigate('/login');
      return;
    }

    // ดึงข้อมูลโปรไฟล์ของผู้ใช้จาก API
    axios
      .get('http://localhost:5000/pawpaw_test/profile', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setUser(response.data); // เก็บข้อมูลโปรไฟล์ลงใน state
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
        alert('Failed to load user profile. Please try again.');
        navigate('/login');
      });

    // ดึงโพสต์ของผู้ใช้จาก API
    axios
      .get(`http://localhost:5000/pawpaw_test/posts/user/${userId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setPosts(response.data.posts); // เก็บโพสต์ลงใน state
      })
      .catch((error) => {
        console.error('Error fetching user posts:', error);
      });
  }, [navigate]);

  // ฟังก์ชันสำหรับเปิด/ปิดโหมดแก้ไข
  const toggleEditMode = () => {
    setIsEditing(!isEditing);
  };

  // ฟังก์ชันสำหรับบันทึกข้อมูลโปรไฟล์ที่แก้ไขแล้ว
  const handleSave = (updatedUser) => {
    setUser(updatedUser);
    setIsEditing(false); // ปิดโหมดแก้ไขหลังจากบันทึก
  };

  // ถ้าผู้ใช้ยังไม่ได้ข้อมูล หรือไม่ได้ล็อกอินให้ไม่แสดงอะไร
  if (!user) {
    return null;
  }

  return (
    <div style={styles.container}>
      <div style={styles.sidebar}>
        {/* ถ้าไม่อยู่ในโหมดแก้ไข ให้แสดงข้อมูลโปรไฟล์ */}
        {!isEditing ? (
          <>
            <div style={styles.profileHeader}>
              <img
                src={user.profileImage || '/default-profile.png'}
                alt="Profile"
                style={styles.profileImage}
              />
              <h2>{user.username}</h2>
            </div>
            <div style={styles.aboutSection}>
              <h3>About</h3>
              {user.showEmail && <p><strong>Email:</strong> {user.email}</p>}
              <p><strong>Phone:</strong> {user.phone}</p>
              <p><strong>Bio:</strong> {user.bio}</p>
              <p><strong>Birthday:</strong> {user.birthday ? new Date(user.birthday).toLocaleDateString() : 'Not provided'}</p>
              <p><strong>Pet Types:</strong> {user.petTypes.length > 0 ? user.petTypes.join(', ') : 'No pets added'}</p>
            </div>
            <button style={styles.editButton} onClick={toggleEditMode}>
              Edit Profile
            </button>
          </>
        ) : (
          // ถ้าอยู่ในโหมดแก้ไข ให้แสดง EditProfile component
          <EditProfile user={user} onSave={handleSave} onCancel={toggleEditMode} />
        )}
      </div>

      <div style={styles.posts}>
        <h3>Posts</h3>
        {/* แสดงโพสต์ของผู้ใช้ */}
        {posts && posts.length > 0 ? (
          posts.map((post) => (
            <Post key={post._id} post={post} /> // ใช้ Post component แสดงโพสต์
          ))
        ) : (
          <p>No posts available. Try posting something about your pet!</p>
        )}
      </div>
    </div>
  );
};

// สไตล์สำหรับหน้าต่างๆ ใน Profile
const styles = {
  container: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '20px',
  },
  sidebar: {
    width: '30%',
    backgroundColor: '#f4f4f4',
    padding: '20px',
    borderRadius: '8px',
  },
  profileHeader: {
    textAlign: 'center',
    marginBottom: '20px',
  },
  profileImage: {
    borderRadius: '50%',
    width: '120px',
    height: '120px',
    objectFit: 'cover',
  },
  aboutSection: {
    marginTop: '20px',
  },
  posts: {
    width: '65%',
    paddingLeft: '20px',
  },
  editButton: {
    marginTop: '20px',
    padding: '10px 20px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    cursor: 'pointer',
    borderRadius: '5px',
  },
};

export default Profile;
