import React, { useState } from 'react';
import { Box, Typography, TextField, Button } from '@mui/material';
import axios from 'axios';

const Comment = ({ comment, postId, fetchComments, currentUserId }) => {
  const [reply, setReply] = useState('');
  const [showReplyBox, setShowReplyBox] = useState(false);

  // Handle reply submission
  const handleReplySubmit = async (parentCommentId) => {
    if (reply.trim() === '') return;
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please log in to reply.');
        return;
      }

      const response = await axios.post(
        `http://localhost:5000/pawpaw_test/comments/${parentCommentId}/reply`,
        { content: reply },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (response.data.success) {
        fetchComments();
        setReply('');
        setShowReplyBox(false);
      }
    } catch (error) {
      console.error('Failed to reply to comment:', error);
    }
  };

  const handleToggleReplyBox = (commentId) => {
    setShowReplyBoxes((prev) => ({
      ...prev,
      [commentId]: !prev[commentId], // สลับการแสดงช่องรีพลายสำหรับ commentId นี้
    }));
  };

  return (
    <Box sx={{ marginLeft: '16px', marginTop: '8px' }}>
      <Typography variant="body1" sx={{ fontWeight: 'bold' }}>{comment.userId.username}</Typography>
      <Typography variant="body2">{comment.content}</Typography>

      <Button onClick={() => setShowReplyBox(!showReplyBox)} variant="outlined" size="small">Reply</Button>

      {showReplyBoxes[comment._id] && (
        <Box sx={{ marginTop: '16px', display: 'flex', alignItems: 'center', gap: '8px' }}>
          <TextField
            placeholder="Write a reply..."
            variant="outlined"
            size="small"
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            sx={{
              flexGrow: 1, // ทำให้ TextField ขยายตัวในพื้นที่ที่เหลือ
              marginRight: '8px', // เพิ่มช่องว่างระหว่าง TextField และปุ่ม
            }}
          />
          <Button variant="contained" color="primary" onClick={handleAddComment}>
            Reply
          </Button>
        </Box>
      )}

      {/* Show replies if any */}
      {comment.replies && comment.replies.length > 0 && (
        <Box sx={{ marginLeft: '16px' }}>
          {comment.replies.map((reply) => (
            <Comment
              key={reply._id}
              comment={reply}
              postId={postId}
              fetchComments={fetchComments}
              currentUserId={currentUserId}
            />
          ))}
        </Box>
      )}
    </Box>
  );
};

export default Comment;
