import React, { useState } from 'react';
import { Card, CardContent, CardActions, Button, TextField, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import ImageUpload from './ImageUpload';
import axios from 'axios';

const CreatePost = ({ onAddPost }) => {
  const [content, setContent] = useState('');
  const [image, setImage] = useState([]);
  const navigate = useNavigate();

  const handleImageChange = (imageList) => {
    setImage(imageList[0]?.file); // Store the selected image (only one image)
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login to create a post');
      navigate('/login');
      return;
    }
  
    try {
      const formData = new FormData();
      formData.append('content', content);  // เพิ่มเนื้อหาของโพสต์
      if (image) {
        formData.append('image', image);  // ส่งไฟล์รูปภาพ
      }
  
      const response = await axios.post(
        'http://localhost:5000/pawpaw/create', 
        formData, 
        { 
          headers: { 
            'Content-Type': 'multipart/form-data',  // กำหนด Content-Type สำหรับการส่งไฟล์
            Authorization: `Bearer ${token}`,      // ส่ง token ไปใน header
          }
        }
      );
      
      if (response.data.success) {
        const newPost = {
          userId: response.data.post.userId,
          content: response.data.post.content,
          images: response.data.post.images,
          likes: response.data.post.likes,
          createdAt: response.data.post.createdAt,
        };
        onAddPost(newPost);  // เพิ่มโพสต์ใหม่ไปที่หน้าหลัก
        setContent('');      // ล้างเนื้อหาหลังจากโพสต์
        setImage(null);      // ล้างภาพ
      }
    } catch (error) {
      console.error('Failed to create post', error);
    }
  };
  
  return (
    <Card sx={{ maxWidth: 600, margin: '20px auto', borderRadius: '12px', boxShadow: 3 }}>
      <CardContent>
        <Typography variant="h5" gutterBottom>
          Create a Post
        </Typography>
        <TextField
          label="What's on your mind?"
          multiline
          rows={4}
          value={content}
          onChange={(e) => setContent(e.target.value)}
          fullWidth
          sx={{ marginBottom: '16px' }}
        />
        <ImageUpload onImagesChange={handleImageChange} />
        {image && (
          <Typography variant="body2" color="textSecondary" sx={{ marginTop: '8px' }}>
            Image Selected: {image.name}
          </Typography>
        )}
      </CardContent>
      <CardActions sx={{ justifyContent: 'flex-end' }}>
        <Button 
          variant="contained" 
          color="primary" 
          onClick={handleSubmit} 
          sx={{ padding: '10px 20px' }}
        >
          Post
        </Button>
      </CardActions>
    </Card>
  );
};

export default CreatePost;
