import React from 'react';
import { Box, Typography, IconButton, Avatar } from '@mui/material';
import { Favorite, Share, Comment, Edit, Delete } from '@mui/icons-material';

const Post = ({ post, isLoggedIn }) => {
    const { content, userId, images } = post;

    return (
        <Box sx={{ padding: '1rem', marginBottom: '1rem', borderRadius: '8px', border: '1px solid #ddd' }}>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <Avatar src={userId.picture} alt={userId.username} />
                <Typography variant="h6" sx={{ marginLeft: '1rem' }}>{userId.username}</Typography>
            </Box>
            <Typography sx={{ marginTop: '1rem', marginBottom: '1rem' }}>{content}</Typography>
            {images && images.map((img, index) => (
                <Box key={index} component="img" src={img} alt={`post-${index}`} sx={{ width: '100%', maxHeight: '300px', borderRadius: '8px', marginBottom: '1rem' }} />
            ))}
            <Box sx={{ display: 'flex', gap: '1rem' }}>
                <IconButton><Favorite /></IconButton>
                <IconButton><Comment /></IconButton>
                <IconButton><Share /></IconButton>
                {isLoggedIn && post.userId === userId._id && (
                    <>
                        <IconButton><Edit /></IconButton>
                        <IconButton><Delete /></IconButton>
                    </>
                )}
            </Box>
        </Box>
    );
};

export default Post;
