import React, { useState, useEffect } from 'react';
import { Box, Typography, Fab } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import Post from '../components/Post';
import CreatePost from '../components/CreatePost';

const Forum = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    // Sample initial posts
    const samplePosts = [
      { id: 1, username: 'User1', content: 'This is a sample post', time: 'April 6, 3:00PM' },
      { id: 2, username: 'User2', content: 'Another post example', time: 'April 6, 4:30PM' },
    ];
    setPosts(samplePosts);
  }, []);

  const handleAddPost = (newPost) => {
    setPosts([newPost, ...posts]); // Add new post at the top
  };

  return (
    <Box sx={{ padding: '20px' }}>
      <Typography variant="h4" sx={{ mb: 2 }}>
        Forum
      </Typography>
      <CreatePost onAddPost={handleAddPost} />
      <Box sx={{ width: '100%', maxWidth: '800px' }}>
        {posts.map((post) => (
          <Post key={post.id} post={post} />
        ))}
      </Box>
      <Fab
        color="primary"
        aria-label="add"
        onClick={() => alert('Add new functionality coming soon')}
        sx={{ position: 'fixed', bottom: 20, right: 20 }}
      >
        <AddIcon />
      </Fab>
    </Box>
  );
};

export default Forum;
