import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './components/Home';
import Profile from './components/Profile';
import Forum from './components/Forum';
import Blog from './components/Blog';
import Sidebar from './components/Sidebar';
import Header from './components/Header';
import { Box } from '@mui/material';

const App = () => {
  const [sidebarOpen, setSidebarOpen] = useState(true); // State to manage sidebar

  const toggleSidebar = () => {
    setSidebarOpen(prevState => !prevState); // Toggle sidebar open/closed
  };

  return (
    <Router>
      <Box sx={{ display: 'flex' }}>
        <Sidebar open={sidebarOpen} toggleSidebar={toggleSidebar} />
        <Box
          sx={{
            flex: 1,
            padding: '20px',
            marginLeft: sidebarOpen ? '240px' : '70px', // Adjust margin based on sidebar state
            transition: 'margin-left 0.3s',
          }}
        >
          <Header />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="/forum" element={<Forum />} />
            <Route path="/blog" element={<Blog />} />
          </Routes>
        </Box>
      </Box>
    </Router>
  );
};

export default App;
