import React, { useState } from 'react';
import { Box, TextField, Button, Avatar, Typography } from '@mui/material';
import Post from './Post';
import { useUser } from './UserContext'; // ใช้ useUser จาก UserContext
import { useNavigate } from 'react-router-dom';

const Forum = () => {
  const { user } = useUser(); // รับ user จาก context
  const navigate = useNavigate();

  const [posts, setPosts] = useState([
    // ตัวอย่างโพสต์
    { id: 1, content: 'First post', username: 'User1' },
    { id: 2, content: 'Second post', username: 'User2' },
    // เพิ่มโพสต์ตัวอย่างได้ตามต้องการ
  ]);
  const [postContent, setPostContent] = useState('');

  // ฟังก์ชันในการโพสต์ใหม่
  const handlePostSubmit = (e) => {
    e.preventDefault();
    if (!user) {
      alert("Please log in to post.");
      return;
    }
    if (postContent) {
      const newPost = {
        id: posts.length + 1,
        content: postContent,
        username: user.username,
        profilePicture: user.profilePicture || '',
      };
      setPosts([newPost, ...posts]);
      setPostContent('');
    }
  };

  // ฟังก์ชันแสดงโพสต์ (จำกัดสำหรับผู้ใช้ที่ไม่ได้ล็อกอิน)
  const displayedPosts = user ? posts : posts.slice(0, 10); // แสดงโพสต์ 10 โพสต์แรกหากยังไม่ล็อกอิน

  return (
    <Box>
      {/* แสดงโปรไฟล์ผู้ใช้หากล็อกอิน */}
      {user && (
        <Box display="flex" alignItems="center" mb={2}>
          <Avatar src={user.profilePicture} alt={user.username} />
          <Typography variant="h6" ml={1}>{user.username}</Typography>
        </Box>
      )}

      {/* ฟอร์มการโพสต์ใหม่ */}
      {user ? (
        <form onSubmit={handlePostSubmit}>
          <TextField
            label="What's on your mind?"
            value={postContent}
            onChange={(e) => setPostContent(e.target.value)}
            fullWidth
          />
          <Button type="submit" variant="contained" color="primary" sx={{ mt: 1 }}>
            Post
          </Button>
        </form>
      ) : (
        <Typography variant="body1" color="textSecondary" mt={2}>
          Please log in to post.
        </Typography>
      )}

      {/* แสดงโพสต์ */}
      <Box mt={2}>
        {displayedPosts.map((post) => (
          <Post key={post.id} post={post} />
        ))}
        {!user && posts.length > 10 && (
          <Typography
            variant="body2"
            color="primary"
            onClick={() => navigate('/login')}
            sx={{ cursor: 'pointer', mt: 2 }}
          >
            Log in to see more posts.
          </Typography>
        )}
      </Box>
    </Box>
  );
};

export default Forum;
