//
import React, { useState } from 'react';
import { TextField, Button, Typography, IconButton } from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import ReplyIcon from '@mui/icons-material/Reply';
import IosShareIcon from '@mui/icons-material/IosShare';
import axios from 'axios';

const Comment = ({ postId, comments, onAddComment }) => {  // Use props as parameters here
  const [comment, setComment] = useState('');

  const handleCommentSubmit = async () => {
    if (comment.trim() !== '') {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          alert('Please log in to comment.');
          return;
        }

        // Send comment to backend
        const response = await axios.post(`http://localhost:5000/pawpaw_test/posts/${postId}/comment`, {
          content: comment
        }, {
          headers: { Authorization: `Bearer ${token}` }
        });

        if (response.data.success) {
          onAddComment(response.data.comment);  // Use the onAddComment prop to add the new comment
          setComment('');  // Clear the comment input field
        }
      } catch (error) {
        console.error('Failed to create comment:', error);
      }
    }
  };

  return (
    <div>
      {comments.map((comment) => (
        <div key={comment._id} style={{ marginTop: '1rem', padding: '10px', border: '1px solid #e0e0e0', borderRadius: '5px' }}>
          <Typography variant="body2" sx={{ marginBottom: '0.5rem' }}>
            {comment.username}: {comment.content}
          </Typography>
          <div>
            <IconButton aria-label="like">
              {comment.likedByUser ? <FavoriteIcon /> : <FavoriteBorderIcon />}
            </IconButton>
            <IconButton aria-label="reply">
              <ReplyIcon />
            </IconButton>
            <IconButton aria-label="share">
              <IosShareIcon />
            </IconButton>
          </div>
        </div>
      ))}
      <TextField
        label="Write a comment..."
        variant="outlined"
        value={comment}
        onChange={(e) => setComment(e.target.value)}
        size="small"
        fullWidth
        sx={{ marginTop: '0.5rem' }}
      />
      <Button variant="contained" color="primary" onClick={handleCommentSubmit} sx={{ marginTop: '0.5rem' }}>
        Comment
      </Button>
    </div>
  );
};

export default Comment;
