import React, { useState, useEffect, memo } from 'react';
import { TextField, Button, Typography, IconButton } from '@mui/material';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';
import LikeButton from './LikeButton';
import axios from 'axios';

const Comment = ({ postId, initialComments, currentUserId }) => {
  const [comments, setComments] = useState(initialComments || []);
  const [replyInputs, setReplyInputs] = useState({});
  const [showReplyFields, setShowReplyFields] = useState({});

  // eslint-disable-next-line
  const [commentState, setCommentState] = useState({
    ...comments,
    likes: Array.isArray(comments.likes) ? comments.likes : [] // ตรวจสอบว่า likes เป็น array ถ้าไม่ใช่ให้กำหนดเป็น array เปล่า
  });

  const fetchComments = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/pawpaw_test/posts/${postId}/comments`);
      if (response.data.success) {
        setComments(response.data.comments);
      }
    } catch (error) {
      console.error('Failed to fetch comments:', error);
    }
  };
  
  // Fetch comments when component mounts
  useEffect(() => {
    if (!initialComments) {
      fetchComments();
    }
    // eslint-disable-next-line
  }, [initialComments]);
  // eslint-disable-next-line

  // Handle submitting a reply to a comment
  const handleReplySubmit = async (commentId) => {
    const replyContent = replyInputs[commentId];
    if (replyContent && replyContent.trim() !== '') {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          alert('Please log in to reply.');
          return;
        }

        // Sending reply to the backend
        const response = await axios.post(
          `http://localhost:5000/pawpaw_test/posts/${postId}/comment`,
          {
            content: replyContent,
            parentCommentId: commentId, // Specify parentCommentId to indicate it's a reply
          },
          { headers: { Authorization: `Bearer ${token}` } }
        );

        if (response.data.success) {
          // Fetch comments again to get the updated list
          await fetchComments();

          // Clear the reply input field
          setReplyInputs((prevState) => ({
            ...prevState,
            [commentId]: '', // Clear reply input field
          }));
        }
      } catch (error) {
        console.error('Failed to create reply:', error);
      }
    }
  };

  // Handle like or unlike a comment
  const handleLikeComment = async (commentId) => {
    try {
      console.log("Comment ID:", commentId); // ตรวจสอบค่า commentId
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please log in to like the comment.');
        return;
      }

      const response = await axios.post(
        `http://localhost:5000/pawpaw_test/comments/${commentId}/like`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (response.data.success) {
        // Update comments state
        setComments((prevComments) =>
          prevComments.map((comment) =>
            comment._id === commentId ? { ...comment, likes: response.data.likes } : comment
          )
        );
      }
    } catch (error) {
      console.error('Failed to like/unlike comment:', error);
    }
  };

  


  // Toggle reply input field visibility
  const toggleReplyField = (commentId) => {
    setShowReplyFields((prevState) => ({
      ...prevState,
      [commentId]: !prevState[commentId],
    }));
  };

  // Memoized comment card component
  const CommentCard = memo(({ comment }) => {
    return (
      <div
        style={{
          marginTop: '1rem',
          padding: '10px',
          border: '1px solid #e0e0e0',
          borderRadius: '5px',
        }}
      >
        {/* Username, Date, and Time */}
        <Typography variant="subtitle2" color="textSecondary">
          {comment.userId?.username ? comment.userId.username : 'Unknown'} •{' '}
          {new Date(comment.createdAt).toLocaleDateString()} •{' '}
          {new Date(comment.createdAt).toLocaleTimeString()}
        </Typography>

        {/* Content */}
        <Typography variant="body1" sx={{ marginBottom: '0.5rem' }}>
          {comment.content}
        </Typography>

        {/* Actions */}
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <LikeButton
            likedByUser={comment.likes && comment.likes.includes(currentUserId)}
            onLike={() => handleLikeComment(comment._id)}
            likeCount={comment.likes.length}
          />
          <IconButton
            aria-label="reply"
            onClick={() => toggleReplyField(comment._id)}
            style={{ marginLeft: '0.5rem' }}
          >
            <ReviewsIcon />
          </IconButton>
          <IconButton aria-label="share" style={{ marginLeft: '0.5rem' }}>
            <IosShareIcon />
          </IconButton>
        </div>

        {/* Reply input field */}
        {showReplyFields[comment._id] && (
          <div
            style={{
              display: 'flex',
              alignItems: 'center',
              marginLeft: '1rem',
              marginTop: '1rem',
            }}
          >
            <TextField
              label="Write a reply..."
              variant="outlined"
              value={replyInputs[comment._id] || ''}
              onChange={(e) =>
                setReplyInputs((prevState) => ({
                  ...prevState,
                  [comment._id]: e.target.value,
                }))
              }
              size="small"
              sx={{ flexGrow: 1, marginRight: '0.5rem' }}
              autoFocus
            />
            <Button
              variant="contained"
              color="primary"
              onClick={() => handleReplySubmit(comment._id)}
            >
              Reply
            </Button>
          </div>
        )}

        {/* Display replies recursively */}
        {comment.replies && comment.replies.length > 0 && (
          <div style={{ marginLeft: '2rem', marginTop: '1rem' }}>
            {comment.replies.map((reply) => (
              <CommentCard key={reply._id} comment={reply} />
            ))}
          </div>
        )}
      </div>
    );
  });

  return (
    <div>
      {comments.length > 0 ? (
        comments.map((comment) => (
          <React.Fragment key={comment._id}>
            <CommentCard comment={comment} />
          </React.Fragment>
        ))
      ) : (
        <Typography variant="body1" sx={{ marginTop: '1rem' }}>
          No comments available.
        </Typography>
      )}
    </div>
  );
};

export default Comment;
