//Post.js
import React, { useState } from 'react';
import { Card, CardContent, CardActions, IconButton, Typography, Grid } from '@mui/material';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';
import Comment from './Comment';
import LikeButton from './LikeButton';
import axios from 'axios';

const Post = ({ post, onLike, likedByUser, onComment, currentUserId }) => {
  const [comments, setComments] = useState(post.comments || []);
  const [showCommentBox, setShowCommentBox] = useState(false);

  useEffect(() => {
    // ดึง userId จาก localStorage เมื่อ component ถูกสร้าง
    const userId = localStorage.getItem('userId');
    setCurrentUserId(userId);
  }, []);

  const handleCommentClick = () => {
    setShowCommentBox(!showCommentBox);
  };

  const handleAddComment = (newComment) => {
    setComments((prevComments) => [newComment, ...prevComments]);
  };

  const handleLike = async () => {
    // เพิ่มการเชื่อมต่อกับ backend เพื่อลงทะเบียนการกดไลค์
    try {
      // เรียก API สำหรับการกดไลค์โพสต์
      await axios.post(`http://localhost:5000/pawpaw_test/posts/${post._id}/like`);
    } catch (error) {
      console.error('Failed to like the post', error);
    }
  };

  return (
    <Card key={post._id} sx={{ maxWidth: 600, margin: '20px auto' }}>
      <CardContent>
        {/* ใช้ userId.username แทน post.username */}
        <Typography variant="subtitle1" color="textSecondary">
          {post.userId.username} • {new Date(post.createdAt).toLocaleDateString()}
        </Typography>
        <Typography variant="body1" sx={{ marginTop: '1rem' }}>
          {post.content}
        </Typography>

        {post.images && post.images.length > 0 && (
          <Grid container spacing={1} sx={{ marginTop: '1rem' }}>
            {post.images.map((image, index) => (
              <Grid item xs={6} key={index}>
                <img src={image.url} alt="" style={{ width: '100%', borderRadius: '4px' }} />
              </Grid>
            ))}
          </Grid>
        )}
      </CardContent>
      <CardActions>
        <IconButton aria-label="like">
        <LikeButton likedByUser={post.likes.includes(currentUserId)} onLike={handleLike} likeCount={post.likes.length}/>
        </IconButton>
        <IconButton aria-label="comment" onClick={handleCommentClick}>
          <ReviewsIcon />
        </IconButton>
        <IconButton aria-label="share">
          <IosShareIcon />
        </IconButton>
      </CardActions>

      {showCommentBox && (
        <CardContent>
          <Comment postId={post._id} comments={comments} onAddComment={handleAddComment} />
        </CardContent>
      )}
    </Card>
  );
};

export default Post;
