import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Post from '../components/Post'; // นำ component Post มาใช้เพื่อแสดงโพสต์ในหน้าโปรไฟล์

const Profile = () => {
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // ดึง token จาก LocalStorage
    const token = localStorage.getItem('token');

    // ตรวจสอบว่ามี token หรือไม่ ถ้าไม่มีให้พาไปหน้า login ทันที
    if (!token) {
      alert('You are not logged in. Please log in first.');
      navigate('/login'); // พาไปที่หน้า login
      return;
    }

    // ถ้ามี token ให้เรียก API เพื่อดึงข้อมูลผู้ใช้
    axios.get('http://localhost:5000/pawpaw_test/profile', {
      headers: {
        Authorization: `Bearer ${token}` // เพิ่ม Bearer token ใน header
      }
    })
    .then(response => {
      setUser(response.data);
      // ดึงข้อมูลโพสต์ของผู้ใช้หลังจากที่ได้ userId มาแล้ว
      return axios.get(`http://localhost:5000/pawpaw_test/posts/user/${response.data._id}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
    })
    .then(response => {
      setPosts(response.data.posts); // ดึง posts จาก response.data.posts
    })
    .catch(error => {
      console.error('Error fetching user data or posts:', error);
      alert('Invalid token. Please log in again.');
      navigate('/login');
    });

  }, [navigate]);

  if (!user) {
    return null; // ไม่แสดงอะไรถ้ายังไม่มีข้อมูลผู้ใช้หรือยังไม่ได้ล็อกอิน
  }

  return (
    <div style={styles.container}>
      <div style={styles.sidebar}>
        <div style={styles.profileHeader}>
          <img src={user.image || '/default-profile.png'} alt="Profile" style={styles.profileImage} />
          <h2>{user.username}</h2>
          <p>{user.profession || 'No profession provided'}</p>
        </div>
        <div style={styles.aboutSection}>
          <h3>About</h3>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Phone:</strong> {user.phone || 'No phone number provided'}</p>
          <p><strong>Location:</strong> {user.location || 'No location provided'}</p>
        </div>
      </div>

      <div style={styles.posts}>
        <h3>Posts</h3>
        {posts && posts.length > 0 ? (
          posts.map(post => (
            <Post key={post._id} post={post} /> // ใช้ Component Post ในการแสดงโพสต์
          ))
        ) : (
          <p>No posts available. Try posting something about your pet!</p>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '20px',
  },
  sidebar: {
    width: '30%',
    backgroundColor: '#f4f4f4',
    padding: '20px',
    borderRadius: '8px',
  },
  profileHeader: {
    textAlign: 'center',
    marginBottom: '20px',
  },
  profileImage: {
    borderRadius: '50%',
    width: '120px',
    height: '120px',
    objectFit: 'cover',
  },
  aboutSection: {
    marginTop: '20px',
  },
  posts: {
    width: '65%',
    paddingLeft: '20px',
  },
};

export default Profile;
