import React, { useState, useEffect } from 'react';
import { Box, Typography, IconButton, TextField, Button, Card } from '@mui/material';
import ReviewsIcon from '@mui/icons-material/Reviews';
import axios from 'axios';
import Reply from './Reply'; // Import Reply component

const Comment = ({ postId }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [showReplyBox, setShowReplyBox] = useState({}); // Track which comment's reply box to show
  const [currentUserId, setCurrentUserId] = useState(localStorage.getItem('userId'));

  // Fetch comments
  useEffect(() => {
    fetchComments();
  }, []);

  const fetchComments = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/pawpaw_test/posts/${postId}/comments`);
      setComments(response.data.comments);
    } catch (error) {
      console.error('Error fetching comments:', error);
    }
  };

  // Handle adding a new comment
  const handleAddComment = async () => {
    if (newComment.trim() === '') return;
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `http://localhost:5000/pawpaw_test/posts/${postId}/comment`,
        { content: newComment },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setComments([response.data.comment, ...comments]);
      setNewComment('');
    } catch (error) {
      console.error('Error adding comment:', error);
    }
  };

  // Handle reply toggle (show/hide reply box)
  const toggleReplyBox = (commentId) => {
    setShowReplyBox((prevState) => ({
      ...prevState,
      [commentId]: !prevState[commentId], // Toggle visibility
    }));
  };

  return (
    <Card sx={{ maxWidth: 800, margin: '20px auto', borderRadius: '16px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', padding: '16px' }}>
      <Typography variant="h6" sx={{ fontWeight: 'bold' }}>Comments</Typography>

      {/* Add a new comment */}
      <Box sx={{ marginTop: '16px' }}>
        <TextField
          placeholder="Write a comment..."
          variant="outlined"
          fullWidth
          multiline
          rows={3}
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          sx={{ marginBottom: '8px' }}
        />
        <Button variant="contained" color="primary" onClick={handleAddComment}>Post Comment</Button>
      </Box>

      {/* Display comments */}
      {comments.map((comment) => (
        <Box key={comment._id} sx={{ marginTop: '16px', padding: '8px', border: '1px solid #e0e0e0', borderRadius: '8px' }}>
          <Typography variant="subtitle2" color="textSecondary">
            {comment.userId?.username || 'Unknown User'} • {new Date(comment.createdAt).toLocaleDateString()} • {new Date(comment.createdAt).toLocaleTimeString()}
          </Typography>
          <Typography variant="body1" sx={{ margin: '8px 0' }}>{comment.content}</Typography>

          {/* Actions (Like, Reply) */}
          <Box sx={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <IconButton onClick={() => toggleReplyBox(comment._id)} aria-label="reply">
              <ReviewsIcon />
            </IconButton>
          </Box>

          {/* Show reply input box if toggled */}
          {showReplyBox[comment._id] && (
            <Box sx={{ marginTop: '8px' }}>
              <TextField
                placeholder="Write a reply..."
                variant="outlined"
                fullWidth
                size="small"
                sx={{ marginBottom: '8px' }}
              />
              <Button variant="contained" color="primary" sx={{ borderRadius: '16px' }}>Reply</Button>
            </Box>
          )}

          {/* Display nested replies */}
          {comment.replies && comment.replies.length > 0 && (
            <Box sx={{ marginLeft: '16px', marginTop: '16px' }}>
              <Reply comment={comment} postId={postId} fetchComments={fetchComments} />
            </Box>
          )}
        </Box>
      ))}
    </Card>
  );
};

export default Comment;
