import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Post from '../components/Post';
import PostForm from '../components/PostForm';
import { Box, Typography } from '@mui/material';

const Forum = ({ isLoggedIn, userProfile }) => {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        const fetchPosts = async () => {
            const response = await axios.get('http://localhost:5000/pawpaw_test/posts');
            setPosts(response.data);
        };
        fetchPosts();
    }, []);

    const handlePostSubmit = async (newPostContent) => {
        // Send a new post to the backend
        const response = await axios.post('http://localhost:5000/pawpaw_test/post', {
            content: newPostContent,
            userId: userProfile.id
        });
        setPosts([response.data, ...posts]);
    };

    return (
        <Box>
            <Typography variant="h4" sx={{ marginBottom: '2rem' }}>Forum</Typography>
            {isLoggedIn && (
                <PostForm onSubmit={handlePostSubmit} userProfile={userProfile} />
            )}
            {posts.map((post) => (
                <Post key={post._id} post={post} isLoggedIn={isLoggedIn} />
            ))}
        </Box>
    );
};

export default Forum;
