import React, { useState, useEffect } from 'react';
import { Card, Box, Typography, TextField, Button, IconButton } from '@mui/material';
import ReviewsIcon from '@mui/icons-material/Reviews';
import axios from 'axios';
import LikeButton from './LikeButton';
import DeleteButton from './DeleteButton';
import Comment from './Comment';  // แสดงคอมเมนต์

const Post = ({ post }) => {
  const [comments, setComments] = useState([]);
  const [currentUserId, setCurrentUserId] = useState(null);
  const [comment, setComment] = useState('');
  const [postState, setPostState] = useState({
    ...post,
    likes: Array.isArray(post.likes) ? post.likes : [],
  });

  // Fetch user ID and comments when the component mounts
  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      setCurrentUserId(userId);
    } else {
      console.error('User ID is not set in localStorage');
    }

    const fetchComments = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/pawpaw_test/posts/${postState._id}/comments`);
        setComments(response.data.comments);
      } catch (error) {
        console.error('Failed to fetch comments:', error);
      }
    };
    
    fetchComments();
  }, [postState._id]);

  // Handle like for post
  const handleLikePost = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please log in to like the post.');
        return false;
      }
  
      const response = await axios.post(
        `http://localhost:5000/pawpaw_test/posts/${postState._id}/like`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
  
      if (response.data.success) {
        setPostState((prevState) => ({
          ...prevState,
          likes: response.data.likes,
        }));
        return true;
      }
    } catch (error) {
      console.error('Failed to like/unlike the post', error);
    }
    return false;
  };

  // Handle comment submission
  const handleCommentSubmit = async () => {
    if (comment.trim() !== '') {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          alert('Please log in to comment.');
          return;
        }

        const response = await axios.post(
          `http://localhost:5000/pawpaw_test/posts/${postState._id}/comment`,
          { content: comment },
          { headers: { Authorization: `Bearer ${token}` } }
        );

        if (response.data.success) {
          setComments((prevComments) => [response.data.comment, ...prevComments]);
          setComment('');
        }
      } catch (error) {
        console.error('Failed to create comment:', error);
      }
    }
  };

  return (
    <Card sx={{ maxWidth: 800, margin: '20px auto', borderRadius: '16px', padding: '16px' }}>
      {/* Post Header */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="subtitle1">{postState.userId.username} | {new Date(postState.createdAt).toLocaleString()}</Typography>
        {currentUserId && postState.userId._id === currentUserId && (
          <DeleteButton onDelete={() => alert('Delete Post')} />
        )}
      </Box>

      {/* Post Content */}
      <Box sx={{ display: 'flex', marginTop: '16px' }}>
        {post.images && post.images.length > 0 && (
          <img src={`http://localhost:5000${post.images[0].url}`} alt="Post" style={{ width: '120px', height: '120px', borderRadius: '12px', objectFit: 'cover', marginRight: '16px' }} />
        )}
        <Typography variant="body1" sx={{ flex: 1 }}>{postState.content}</Typography>
      </Box>

      {/* Like Button */}
      <Box sx={{ display: 'flex', alignItems: 'center', marginTop: '16px' }}>
        <LikeButton likedByUser={currentUserId && postState.likes.includes(currentUserId)} onLike={handleLikePost} likeCount={postState.likes.length} />
        <IconButton aria-label="comment" onClick={() => console.log('Comment icon clicked')}>
          <ReviewsIcon />
        </IconButton>
        <TextField
          placeholder="Write a comment..."
          variant="outlined"
          size="small"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          sx={{ flexGrow: 1, backgroundColor: '#ffffff', borderRadius: '16px' }}
        />
        <Button variant="contained" color="primary" onClick={handleCommentSubmit}>Comment</Button>
      </Box>

      {/* Comments Section */}
      {comments.length > 0 && (
        <Box sx={{ marginTop: '16px' }}>
          {comments.map((comment) => (
            <Comment key={comment._id} comment={comment} postId={postState._id} fetchComments={() => {}} currentUserId={currentUserId} />
          ))}
        </Box>
      )}
    </Card>
  );
};

export default Post;
