import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Post from '../components/Post';  // คอมโพเนนต์สำหรับโพสต์
import EditProfile from './EditProfile';  // คอมโพเนนต์สำหรับแก้ไขโปรไฟล์

const Profile = () => {
  const [user, setUser] = useState({});
  const [posts, setPosts] = useState([]);
  const [isEditing, setIsEditing] = useState(false);  // สถานะโหมดการแก้ไข
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userId = localStorage.getItem('userId');

    if (!token || !userId) {
      alert('You are not logged in. Please log in first.');
      navigate('/login');
      return;
    }

    // ดึงข้อมูลโปรไฟล์จาก API
    axios
      .get('http://localhost:5000/pawpaw_test/profile', {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => {
        setUser(response.data);
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
      });

    // ดึงโพสต์ของผู้ใช้
    axios
      .get(`http://localhost:5000/pawpaw_test/posts/user/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => {
        setPosts(response.data.posts);
      })
      .catch((error) => {
        console.error('Error fetching posts:', error);
      });
  }, [navigate]);

  const toggleEditMode = () => {
    setIsEditing(!isEditing);  // สลับโหมดการแก้ไข
  };

  return (
    <div style={styles.container}>
      <div style={styles.sidebar}>
        {isEditing ? (
          <EditProfile user={user} onSave={(updatedUser) => setUser(updatedUser)} />
        ) : (
          <div>
            <h1>Profile</h1>
            <div>
              <img
                src={user.profileImage || '/default-profile.png'}
                alt="Profile"
                style={styles.profileImage}
              />
            </div>
            <div>
              <p><strong>Username:</strong> {user.username}</p>
              <p><strong>Email:</strong> {user.showEmail ? user.email : 'Email Hidden'}</p>
              <p><strong>Phone:</strong> {user.profile?.phone}</p>
              <p><strong>Birthday:</strong> {new Date(user.profile?.birthday).toLocaleDateString()}</p>
              <p><strong>Bio:</strong> {user.profile?.bio}</p>
              <p><strong>Pets:</strong> {user.profile?.petTypes?.join(', ')}</p>
            </div>
            <button onClick={toggleEditMode} style={styles.editButton}>Edit Profile</button>
          </div>
        )}
      </div>

      <div style={styles.posts}>
        <h3>Posts</h3>
        {posts.length > 0 ? (
          posts.map((post) => <Post key={post._id} post={post} />)
        ) : (
          <p>No posts available.</p>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '20px',
  },
  sidebar: {
    width: '30%',
    backgroundColor: '#f4f4f4',
    padding: '20px',
    borderRadius: '8px',
  },
  profileImage: {
    borderRadius: '50%',
    width: '120px',
    height: '120px',
    objectFit: 'cover',
  },
  posts: {
    width: '65%',
    paddingLeft: '20px',
  },
  editButton: {
    marginTop: '20px',
    padding: '10px 20px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    cursor: 'pointer',
    borderRadius: '5px',
  },
};

export default Profile;
