import React from 'react';
import { Box, Typography, Button, Avatar } from '@mui/material';
import { useParams } from 'react-router-dom';

const Profile = ({ currentUser }) => {
  // Assuming we get userId from URL params
  const { userId } = useParams();

  // Check if the profile being viewed is the current user's profile
  const isCurrentUser = userId === currentUser.id;

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', p: 3 }}>
      {/* Profile Information Section */}
      <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <Avatar
          src="https://via.placeholder.com/150" // Replace with user's profile picture
          alt="User Profile"
          sx={{ width: 150, height: 150, mb: 2 }}
        />
        <Typography variant="h4">Charles Deo</Typography>
        <Typography variant="subtitle1" color="textSecondary">
          UI/UX Designer
        </Typography>
        {isCurrentUser ? (
          <Button variant="contained" color="primary" sx={{ mt: 2 }}>
            Edit Profile
          </Button>
        ) : (
          <Button variant="contained" color="secondary" sx={{ mt: 2 }}>
            Follow
          </Button>
        )}
      </Box>

      {/* About Section */}
      <Box sx={{ mt: 4, width: '100%' }}>
        <Typography variant="h6">About</Typography>
        <Typography variant="body1">Male</Typography>
        <Typography variant="body1">Born June 26, 1980</Typography>
        <Typography variant="body1">Hog Camp Road, Schaumburg</Typography>
      </Box>

      {/* Posts Section */}
      <Box sx={{ mt: 4, width: '100%' }}>
        <Typography variant="h6">Posts</Typography>
        {/* Replace this with the list of posts the user has made */}
        <Box sx={{ mt: 2 }}>
          <Typography>Post 1...</Typography>
          <Typography>Post 2...</Typography>
        </Box>
      </Box>
    </Box>
  );
};

export default Profile;
