import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EditProfile = ({ user, onSave }) => {
  const [username, setUsername] = useState(user.username || '');
  const [email, setEmail] = useState(user.email || '');
  const [birthday, setBirthday] = useState(user.profile?.birthday || '');
  const [bio, setBio] = useState(user.profile?.bio || '');
  const [showEmail, setShowEmail] = useState(user.profile?.showEmail || false); // ใช้ showEmail

  useEffect(() => {
    if (user.profile?.birthday) {
      setBirthday(new Date(user.profile.birthday).toLocaleDateString());
    }
  }, [user]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('username', username);
    formData.append('email', email);
    formData.append('birthday', birthday);
    formData.append('bio', bio);
    formData.append('showEmail', showEmail);  // เพิ่ม showEmail

    try {
      const token = localStorage.getItem('token');
      const response = await axios.put('http://localhost:5000/pawpaw_test/profile', formData, {
        headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'multipart/form-data' },
      });
      onSave(response.data);  // อัปเดตข้อมูลโปรไฟล์ในหน้า Profile
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Edit Profile</h2>
      <div>
        <label>Username</label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
      </div>
      <div>
        <label>Email</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>
      <div>
        <label>Birthday</label>
        <input
          type="date"
          value={birthday}
          onChange={(e) => setBirthday(e.target.value)}
        />
      </div>
      <div>
        <label>Bio</label>
        <textarea
          value={bio}
          onChange={(e) => setBio(e.target.value)}
        />
      </div>
      <div>
        <label>Show Email</label>
        <input
          type="checkbox"
          checked={showEmail}
          onChange={(e) => setShowEmail(e.target.checked)}  // ปรับค่าของ showEmail
        />
        <span>{showEmail ? 'Email will be shown' : 'Email will be hidden'}</span>
      </div>
      <button type="submit">Save Changes</button>
    </form>
  );
};

export default EditProfile;
