// Post.js
import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardActions, IconButton, Typography, Grid, TextField, Button } from '@mui/material';
import ReviewsIcon from '@mui/icons-material/Reviews';
import IosShareIcon from '@mui/icons-material/IosShare';
import LikeButton from './LikeButton';
import axios from 'axios';
import Comment from './Comment';

const Post = ({ post }) => {
  const [postState, setPostState] = useState(post);
  const [comments, setComments] = useState([]);
  const [currentUserId, setCurrentUserId] = useState(null);
  const [comment, setComment] = useState('');

  // Fetch user ID and comments when the component mounts
  useEffect(() => {
    const userId = localStorage.getItem('userId');
    setCurrentUserId(userId);
    
    const fetchComments = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/pawpaw_test/posts/${postState._id}/comments`);
        setComments(response.data.comments);  // Set comments from the backend response
      } catch (error) {
        console.error('Failed to fetch comments:', error);
      }
    };
    // eslint-disable-next-line
    fetchComments();
  }, [postState._id]);

  // Function to add a new comment to the state
  const handleAddComment = (newComment) => {
    setComments((prevComments) => [newComment, ...prevComments]);
  };

  // Handle like for post
  const handleLike = async () => {
    try {
      // ตรวจสอบค่า postState._id ก่อนทำการส่งคำขอ
      console.log('Post ID:', postState._id);
      const response = await axios.post(
        `http://localhost:5000/pawpaw_test/posts/${postState._id}/like`,
        {},
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
  
      if (response.data.success) {
        // อัปเดต state ของ post ด้วยข้อมูล likes ล่าสุด
        setPostState((prevState) => ({
          ...prevState,
          likes: response.data.post.likes,

        }));
      }
    } catch (error) {
      console.error('Failed to like/unlike the post', error);
    }
  };
  
  

  // Handle comment submission
  const handleCommentSubmit = async () => {
    if (comment.trim() !== '') {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          alert('Please log in to comment.');
          return;
        }

        // Send the comment to the backend
        const response = await axios.post(`http://localhost:5000/pawpaw_test/posts/${postState._id}/comment`, { content: comment }, {
          headers: { Authorization: `Bearer ${token}` }
        });
        console.log(postState._id)

        // Check if the comment submission was successful
        if (response.data.success) {
          console.log('New Comment:', response.data.comment);
          handleAddComment(response.data.comment);  // Add the new comment to state
          setComment('');  // Clear the comment input field
        }
        
      } catch (error) {
        console.error('Failed to create comment:', error);
      }
    }
  };

  return (
    <Card key={postState._id} sx={{ maxWidth: 600, margin: '20px auto' }}>
      <CardContent>
        {/* Display post username and creation date */}
        <Typography variant="subtitle1" color="textSecondary">
          {postState.userId.username} • {new Date(postState.createdAt).toLocaleDateString()}
        </Typography>
        <Typography variant="body1" sx={{ marginTop: '1rem' }}>
          {postState.content}
        </Typography>

        {postState.images && postState.images.length > 0 && (
          <Grid container spacing={1} sx={{ marginTop: '1rem' }}>
            {postState.images.map((image, index) => (
              <Grid item xs={6} key={index}>
                <img src={image.url} alt="" style={{ width: '100%', borderRadius: '4px' }} />
              </Grid>
            ))}
          </Grid>
        )}
      </CardContent>
      <CardActions sx={{ display: 'flex', alignItems: 'center' }}>
         {/* ใช้ LikeButton Component เพื่อจัดการการไลค์ */}
         <LikeButton
          postId={postState._id}
          likedByUser={postState.likes?.includes(currentUserId)}
          onLike={handleLike}
          likeCount={postState.likes?.length || 0}
        />
        <IconButton aria-label="comment">
          <ReviewsIcon />
        </IconButton>
        <TextField
          label="Write a comment..."
          variant="outlined"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          size="small"
          sx={{ flexGrow: 1, marginRight: '1rem' }}
        />
        <Button variant="contained" color="primary" onClick={handleCommentSubmit}>
          Comment
        </Button>
        <IconButton aria-label="share">
          <IosShareIcon />
        </IconButton>
      </CardActions>
      {comments.length > 0 && (
        <CardContent>
          <Comment postId={postState._id} comments={comments} onAddComment={handleAddComment} />
        </CardContent>
      )}
    </Card>
  );
};

export default Post;
