import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
  const [user, setUser] = useState(null);
  
  const [isFollowing, setIsFollowing] = useState(false);
  const navigate = useNavigate(); // ใช้ useNavigate เพื่อนำผู้ใช้ไปหน้า login

  useEffect(() => {
    const token = localStorage.getItem('token'); // ดึง token จาก LocalStorage
    
    // ตรวจสอบว่ามี token หรือไม่ ถ้าไม่มีให้พาไปหน้า login ทันที
    if (!token) {
      alert('You are not logged in. Please log in first.');
      navigate('/login'); // พาไปที่หน้า login
      return;
    }

    // ถ้ามี token ให้เรียก API เพื่อดึงข้อมูลผู้ใช้
    axios.get('/api/user/profile', {
      headers: {
        Authorization: `Bearer ${token}` // เพิ่ม Bearer token ใน header
      }
    })
    .then(response => {
      setUser(response.data);
    })
    .catch(error => {
      console.error('Error fetching user data:', error);
      // ถ้า token ไม่ถูกต้อง ให้พาไปหน้า login
      alert('Invalid token. Please log in again.');
      navigate('/login');
    });
  }, [navigate]);

  const handleFollow = () => {
    setIsFollowing(!isFollowing);
  };

  if (!user) {
    return null; // ไม่แสดงอะไรถ้ายังไม่มีข้อมูลผู้ใช้หรือยังไม่ได้ล็อกอิน
  }

  return (
    <div style={styles.container}>
      {/* Sidebar */}
      <div style={styles.sidebar}>
        <div style={styles.profileHeader}>
          <img src={user.image || '/default-profile.png'} alt="Profile" style={styles.profileImage} /> {/* ใส่รูปโปรไฟล์ */}
          <h2>{user.username}</h2>
          <p>{user.profession || 'No profession provided'}</p>
          {/* ปุ่มติดตาม */}
          <button style={styles.followButton} onClick={handleFollow}>
            {isFollowing ? 'Unfollow' : 'Follow'}
          </button>
        </div>
        <div style={styles.aboutSection}>
          <h3>About</h3>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Phone:</strong> {user.phone || 'No phone number provided'}</p>
          <p><strong>Location:</strong> {user.location || 'No location provided'}</p>
        </div>
      </div>

      {/* Posts Section */}
      <div style={styles.posts}>
        <h3>Posts</h3>
        {posts && posts.length > 0 ? (
          posts.map(post => (
            <div key={post._id} style={styles.post}>
              <h4>{post.title}</h4>
              <img src={post.image} alt="Post" style={styles.postImage} />
              <div style={styles.postStats}>
                <span>{post.likes} Likes</span>
                <span>{post.comments} Comments</span>
              </div>
            </div>
          ))
        ) : (
          <p>No posts available. Try posting something about your pet!</p>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '20px',
  },
  sidebar: {
    width: '30%',
    backgroundColor: '#f4f4f4',
    padding: '20px',
    borderRadius: '8px',
  },
  profileHeader: {
    textAlign: 'center',
    marginBottom: '20px',
  },
  profileImage: {
    borderRadius: '50%',
    width: '120px',
    height: '120px',
    objectFit: 'cover',
  },
  followButton: {
    marginTop: '10px',
    padding: '10px',
    backgroundColor: '#6c63ff',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  aboutSection: {
    marginTop: '20px',
  },
  posts: {
    width: '65%',
    paddingLeft: '20px',
  },
  post: {
    marginBottom: '20px',
    backgroundColor: '#fff',
    borderRadius: '8px',
    padding: '15px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  },
  postImage: {
    width: '100%',
    height: 'auto',
    borderRadius: '8px',
    marginTop: '10px',
  },
  postStats: {
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: '10px',
  },
};

export default Profile;
